func TestMakeLanguages(t *testing.T) {
	data := Dump{}
	if err := json.Unmarshal([]byte(Languages), &data); err != nil {
		t.Error(err)
	}
	for _, item := range data {
		key := strings.ReplaceAll(item[0], "-", "")
		lang := strings.ToUpper(key[0:1]) + key[1:]
		comma := strings.Index(item[1], "1,234")
		space := strings.Index(item[1], "1 234")
		apo := strings.Index(item[1], "1’234")
		temp := "%s%d"
		if strings.Index(item[1], "$ ") == 0 || strings.Index(item[1], "US$ ") == 0 || strings.Index(item[1], "USD ") == 0 {
			temp = "%s %d"
		}
		if strings.Index(item[1], "$") > 0 || strings.Index(item[1], "US$") > 0 || strings.Index(item[1], "USD") > 0 {
			temp = "%d%s"
		}
		if strings.Index(item[1], " $") > 0 || strings.Index(item[1], " US$") > 0 || strings.Index(item[1], " USD") > 0 {
			temp = "%d %s"
		}
		if space > -1 {
			fmt.Printf(`%s: {Code: %s, Decimal: ",", Thousand: " ", Template: "%s"}, // %s`, lang, lang, temp, item[1])
		} else if apo > -1 {
			fmt.Printf(`%s: {Code: %s, Decimal: ".", Thousand: "’", Template: "%s"}, // %s`, lang, lang, temp, item[1])
		} else if comma > -1 {
			fmt.Printf(`%s: {Code: %s, Decimal: ".", Thousand: ",", Template: "%s"}, // %s`, lang, lang, temp, item[1])
		} else {
			fmt.Printf(`%s: {Code: %s, Decimal: ",", Thousand: ".", Template: "%s"}, // %s`, lang, lang, temp, item[1])
		}
		fmt.Print("\n")
	}
	t.Error("test")
}
